import * as axios from "axios";

const instance = axios.create({
    baseURL: "https://www.mocky.io/v2/",
})

export const citiesAPI = {
    getAllCity() {
        return instance.get('5b34c0d82f00007400376066?mocky-delay=700ms').then(response => response.data)
    },

    getDataByID(id) {
        return instance.get(`${id}?mocky-delay=700ms`).then(response => response.data)
    },
}