import React, {useEffect, useState} from 'react';
import {citiesAPI} from 'apiAxios/apiAxios'

import s from './Form.module.scss';

export const Form = () => {
    const [citiesList, setCitiesList] = useState()
    useEffect(()=> {
        citiesAPI.getAllCity().then(data => setCitiesList(data))
    }, [])

    return (
        <>  {citiesList}
            <form className={s.mainWrapper}>
                <select className={s.inputItem} name="pets" id="pet-select">
                    <option value="">Владивосток</option>
                    <option value="dog">Dog</option>
                </select>
                <div className={s.data_and_time}>
                    <select className={s.inputItem} name="pets" id="pet-select">
                        <option value="">Дата</option>
                        <option value="dog">Dog</option>
                    </select>
                    <select className={s.inputItem} name="pets" id="pet-select">
                        <option value="">Время</option>
                        <option value="dog">Dog</option>
                    </select>
                </div>


                <input className={s.inputItem} type="text" placeholder='+7(___)__ __'/>
                <input className={s.inputItem} type="text" placeholder='Ваше имя'/>


            </form>
            <button className={s.button}>Записаться</button>
        </>
    );
}
