import React from 'react';
import s from 'components/main-logo/MainLogo.module.scss';
import {Loader} from 'components/Loader'


export const MainLogo = ({isLoading}) => {
    return (
        <section className={s.mainWrapper}>

            {!isLoading ? <Loader/> : <img className={s.logoImage} src={require("assets/logoImage.png")} alt="Logo"/>}
            <img className={s.logoName} src={require("assets/logoName.png")} alt="Logo"/>
            <h1>Онлайн запись</h1>

        </section>
    )
}